INSERT INTO `firelands_string` (`entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`) VALUES
(@ENTRY+0, 'This server is running the |cff4CFF00Recruit a friend |rmod.', '', '', '', '', '', 'Este servidor está ejecutando el módulo |cff4CFF00Recruit a friend.', 'Este servidor está ejecutando el módulo |cff4CFF00Recruit a friend.', ''),
(@ENTRY+1, '[RAF]: This module is disabled', '', '', '', '', '', '[RAF]: El módulo está desactivado', '[RAF]: El módulo está desactivado', ''),
(@ENTRY+2, '[RAF]: You have already recruited a friend. If you want to recruit another one, you have to restart with the command : .raf reset', '', '', '', '', '', '[RAF]: Ya has reclutado a un amigo. Si quieres reclutar a otro, tienes que reiniciar con el comando : .raf reset .', '[RAF]: Ya has reclutado a un amigo. Si quieres reclutar a otro, tienes que reiniciar con el comando : .raf reset .', ''),
(@ENTRY+3, '[RAF]: Excellent!, you have recruited in the right way. Close the game, and log in again, for the changes to take effect.', '', '', '', '', '', '[RAF]: Excelente!, has reclutado de la forma correcta. Cierra el juego e inicia sesión de nuevo para que los cambios surtan efecto.', '[RAF]: Excelente, has reclutado de la forma correcta. Cierra el juego e inicia sesión de nuevo para que los cambios surtan efecto.', ''),
(@ENTRY+4, '[RAF]: Excellent!, your recruitment has been reset. Close the game and log back in for the changes to take effect.', '', '', '', '', '', '[RAF]: Excelente tu reclutamiento ha sido reiniciado. Cierra el juego y vuelve a iniciar sesión para que los cambios surtan efecto.', '[RAF]: Excelente tu reclutamiento ha sido reiniciado. Cierra el juego y vuelve a iniciar sesión para que los cambios surtan efecto.', ''),
(@ENTRY+5, '[RAF]: You cannot recruit yourself.', '', '', '', '', '', '[RAF]: No te puedes reclutar a ti mismo.', '[RAF]: No te puedes reclutar a ti mismo.', ''),
(@ENTRY+6, '[RAF]: Recruited character: %s', '', '', '', '', '', '[RAF]: Personaje reclutado: %s', '[RAF]: Personaje reclutado: %s', ''),
(@ENTRY+7, "[RAF] You haven\'t recruited anyone. Use the .raf add command to do this.", '', '', '', '', '', '[RAF] No has reclutado a nadie. Utiliza el comando .raf add para hacerlo.', '[RAF] No has reclutado a nadie. Utiliza el comando .raf add para hacerlo.', '');
